package oop1210;

public class Test02_print {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		//overloading 중복정의 
		// + 연산자 
		
		System.out.println(10+20); // 더하기 
		System.out.println(10+"20"); //연결하기
		System.out.println("10"+20);
		System.out.println("10"+"20");
		System.out.println("10+20");
		
		//" ' \ 등을 단순기호로 인식하고자 할 때 
		
		System.out.println("\"");
		System.out.println("\'");
		System.out.println("\\");
		
		
	}

}
